<journal-article> {
	p:P31  @<P31_instance_of> ;
	p:P577 @<P577_publication_date> ;
	p:P1433 @<P1433_published_in> ;
	p:P1476 @<P1476_title> ;
}

<P31_instance_of> .
<P577_publication_date> .
<P1433_published_in> .
